describe('flaverr.buildOmen()', ()=>{

  describe('with `flaverr.buildOmen()`', ()=>{
    it('should work');
    it('should have `.name === \'Error\'`');
  });

  describe('with `flaverr.buildOmen(caller)`', ()=>{
    it('should work');
    it('should have `.name === \'Error\'`');
    it('should properly modify stack trace');
  });

  describe('resulting omen', ()=>{
    it('should have properly formatted stack trace');
  });

});
